<?phpif ( ! defined( 'ABSPATH' ) ) {	exit;}/** * GAM_Event_Manager_Install */class GAM_Event_Manager_Install {	/**	 * Install WP Job Manager	 */	public static function install() {		global $wpdb;		self::init_user_roles();		self::default_terms();		self::schedule_cron();		// Redirect to setup screen for new installs		if ( ! get_option( 'wp_event_manager_version' ) ) {			set_transient( '_event_manager_activation_redirect', 1, HOUR_IN_SECONDS );		}				// Update legacy options		if ( false === get_option( 'event_manager_submit_event_form_page_id', false ) && get_option( 'event_manager_submit_page_slug' ) ) {			$page_id = get_page_by_path( get_option( 'event_manager_submit_page_slug' ) )->ID;			update_option( 'event_manager_submit_event_form_page_id', $page_id );		}		if ( false === get_option( 'event_manager_event_dashboard_page_id', false ) && get_option( 'event_manager_event_dashboard_page_slug' ) ) {			$page_id = get_page_by_path( get_option( 'event_manager_event_dashboard_page_slug' ) )->ID;			update_option( 'event_manager_event_dashboard_page_id', $page_id );		}		delete_transient( 'wp_event_manager_addons_html' );		update_option( 'wp_event_manager_version', EVENT_MANAGER_VERSION );	}		/**	 * Init user roles	 */	private static function init_user_roles() {		global $wp_roles;		if ( class_exists( 'WP_Roles' ) && ! isset( $wp_roles ) ) {			$wp_roles = new WP_Roles();					}		if ( is_object( $wp_roles ) ) {			add_role( 'organizer', __( 'Organizer', 'gam-event-manager' ), array(				'read'         => true,				'edit_posts'   => false,				'delete_posts' => false			) );			$capabilities = self::get_core_capabilities();			foreach ( $capabilities as $cap_group ) {				foreach ( $cap_group as $cap ) {					$wp_roles->add_cap( 'administrator', $cap );				}			}		}	}	/**	 * Get capabilities	 * @return array	 */	 	private static function get_core_capabilities() {		return array(			'core' => array(				'manage_event_listings'			),			'event_listing' => array(				"edit_event_listing",				"read_event_listing",				"delete_event_listing",				"edit_event_listings",				"edit_others_event_listings",				"publish_event_listings",				"read_private_event_listings",				"delete_event_listings",				"delete_private_event_listings",				"delete_published_event_listings",				"delete_others_event_listings",				"edit_private_event_listings",				"edit_published_event_listings",				"manage_event_listing_terms",								"edit_event_listing_terms",				"delete_event_listing_terms",				"assign_event_listing_terms"			)		);	}	/**	 * default_terms function.	 */	private static function default_terms() {		if ( get_option( 'event_manager_installed_terms' ) == 1 ) {			return;		}		$taxonomies = array(			'event_listing_type' => array(				'Appearance or Signing',				'Attraction',				'Camp, Trip, or Retreat',				'Class, Training, or Workshop',				'Concert or Performance',				'Conference',				'Convention',				'Dinner or Gala',				'Festival or Fair',				'Game or Competition',				'Meeting or Networking Event',				'Other',				'Party or Social Gathering',								'Race or Endurance Event',				'Rally',				'Screening',				'Seminar or Talk',				'Tour',				'Tournament',				'Tradeshow, Consumer Show or Expo'			)		);		foreach ( $taxonomies as $taxonomy => $terms ) {			foreach ( $terms as $term ) {				if ( ! get_term_by( 'slug', sanitize_title( $term ), $taxonomy ) ) {					wp_insert_term( $term, $taxonomy );				}			}		}				update_option( 'event_manager_installed_terms', 1 );	}	/**	 * Setup cron events	 */	private static function schedule_cron() {	    		wp_clear_scheduled_hook( 'event_manager_check_for_expired_events' );				wp_clear_scheduled_hook( 'event_manager_delete_old_previews' );		wp_clear_scheduled_hook( 'event_manager_clear_expired_transients' );		wp_schedule_event( time(), 'hourly', 'event_manager_check_for_expired_events' );		wp_schedule_event( time(), 'daily', 'event_manager_delete_old_previews' );		wp_schedule_event( time(), 'twicedaily', 'event_manager_clear_expired_transients' );	}}